let task2 = function() {
  let str = 'Топ 100 - главный рейтинг книг, который составляется по результатам голосования читателей.';
  //делаем регулярку для того, чтобы получить нужную нам группу символов из исходной строки
  let re = /^.*(главный.*)/;
  return str.match(re)[1]
            .replace(/,/g, '') //можно просто ',' без регулярного выражения
            .replace(/ /g, '+')
            // .replace(/.$/, '!') // можно и так заменить последний элемент
            .slice(0, -1) + '!'
            ;
}

